exports.load = function(ffmpeg) {
  ffmpeg
    .toFormat('mp4')
    .withVideoBitrate('1024')
		.withAspect('16:9')
		.withSize('1280x720')
		.applyAutopadding(true, 'black')
		.withVideoCodec('libx264')
		.withAudioCodec('libvorbis')
		.withAudioChannels(2)
		.withAudioBitrate('128k')
		.withFps(30)
  return ffmpeg;
};
